mapper.select:
SELECT DISTINCT t_driver.driver_id, t_driver.driver_name, t_driver.parent_driver_id AS parent_driver_id, t_driver.driver_address, t_driver.driver_contact, t_parent_driver.driver_name AS parent_driver_name, t_driver.opt_user AS opt_user_id, t_user.user_name AS opt_user_name, t_driver.opt_time FROM base_driver t_driver LEFT JOIN system_users t_user ON t_driver.opt_user = t_user.user_id LEFT JOIN base_driver t_parent_driver ON t_driver.parent_driver_id = t_parent_driver.driver_id WHERE 1=1
AND t_driver.driver_name LIKE CONCAT('%',CONCAT(#{driverName},'%'))
AND t_driver.is_usable = #{isUsable}
LIMIT #{rowIndex},#{rowCount}
